Index.getInitialProps = async () => {
  const response = await fetch("http://localhost:5500/api/letter/1");
  const data = await response.json();
  let textToArray = data[0].text.split("");
  console.log(textToArray);
  const tab = /\t/g;
  const lineBreak = /\r/g;
  for (let i = 0; i < textToArray.length; i++) {
    if (textToArray[i].match(tab)) {
      textToArray[i] = '<span class="pl-4">';
      // console.log(textToArray[i] + "tab");
    } else if (textToArray[i].match(lineBreak)) {
      // textToArray[i - 1] = "</span>";
      textToArray[i] = "</span><p>";
      // console.log(textToArray[i] + "lineBreak");
    }
  }
  console.log(textToArray);
  return { data: data };
};

export default Index;
