        );
      })} */}
    </div>
  );
};

Index.getInitialProps = async () => {
  const response = await fetch("http://localhost:5500/api/letter/1");
  const data = await response.json();
  let formattedData = data[0].text.replace(/\r/g, "<br />");
  let formattedData2 = formattedData.replace(/\t/g, "<span></span>");
  //   data[0].text.replace("morning", "TEST");
  //   console.log(data);
  return { data: formattedData2 };
};

export default Index;
