Index.getInitialProps = async () => {
  const response = await fetch("http://localhost:5500/api/letter/1");
  const data = await response.json();
  let textToArray = data[0].text.split("");
  let testTextToArray =
    '\tMonday morning May 9\rMrs. Jervis\rDear Madam,\rThe Dr. pronounced Mrs. Sullivan’s illness "Gastric fever.” She has passed the crisis, but is still very feeble. Dr. Hines says she must not expect to get along very fast. We hope however that her convalescence will be continuous.\rVery much obliged for what you sent over. With much regard\r\tTruly yours,\r\tJ. H. Sullivan';
  const tab = /\t/g;
  const lineBreak = /\r/g;
  let openParagraph = false;
  for (let i = 0; i < testTextToArray.length; i++) {
    if (testTextToArray[i].match(tab) && openParagraph == false) {
      testTextToArray[i] = '<span class="pl-4">';
      // console.log(testTextToArray[i] + "tab");
    } else if (testTextToArray[i].match(tab) && openParagraph == true) {
      testTextToArray[i] = '</p><span class="pl-4">';
      openParagraph = false;
    } else if (testTextToArray[i].match(lineBreak)) {
      // textToArray[i - 1] = "</span>";
      testTextToArray[i] = "</span><p>";
      openParagraph = true;

      // console.log(testTextToArray[i] + "lineBreak");
    }
  }
  console.log(textToArray);
  return { data: data };
};

export default Index;
