  // <p><span>
  // <p>
  // <span>
  let testTextToArray = testText.split("");
  let shortTextToArray = textToArray.slice(1, 85);
  const tab = /\t/g;
  const lineBreak = /\r/g;
  let paragraphOpen = false;
  let spanOpen = false;
  // console.log(testTextToArray);

  let inputArray = testTextToArray;

  for (let i = 0; i < inputArray.length; i++) {
    if (!spanOpen) {
      if (!paragraphOpen && inputArray[i] == "\t") {
        console.log("HELLO!!!");
      }
      if (inputArray[i] == "\t") {
        spanOpen = true;
        inputArray[i] =
          '<span class="pl-4">FIRST' +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
    }
    if (spanOpen && !paragraphOpen) {
      if (testTextToArray[i] == "\r") {
        spanOpen = false;
        paragraphOpen = true;
        testTextToArray[i] =
          "</span><p>" +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
      if (inputArray[i] == "\t") {
        inputArray[i] =
          '</span><span class="pl-4">' +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
    }
    if (!paragraphOpen) {
      if (inputArray[i] == "\r") {
        paragraphOpen = true;
        inputArray[i] =
          "<p>" +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
    }
    if (paragraphOpen && !spanOpen) {
      if (inputArray[i] == "\r") {
        paragraphOpen = true;
        inputArray[i] =
          "</p><p>" +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
      if (inputArray[i] == "\t" && inputArray[i + 1] == "\r") {
        paragraphOpen = true;
        inputArray[i] =
          "</p>" +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
    }
    if (i == inputArray.length - 1) {
      if (paragraphOpen && spanOpen) {
        inputArray.push("</span></p>");
        paragraphOpen = false;
        spanOpen = false;
      }
      if (paragraphOpen) {
        inputArray.push("</p>");
        paragraphOpen = false;
      }
      if (spanOpen) {
        inputArray.push("</span>");
        spanOpen = false;
      }
    }
    if (spanOpen && paragraphOpen) {
      if (inputArray[i] == "\r") {
        paragraphOpen = false;
        spanOpen = false;
        inputArray[i] =
          "</span></p>" +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
    }
    if (!spanOpen && !paragraphOpen) {
      if (inputArray[i] == "\t" && inputArray[i - 1] == "\r") {
        console.log("FALSE FALSE");
        paragraphOpen = false;
        spanOpen = false;
        inputArray[i] =
          "<p>" +
          " " +
          "spanOpen: " +
          spanOpen.toString() +
          " & " +
          "paragraphOpen: " +
          paragraphOpen.toString();
      }
    }
  }

  console.log(inputArray);
  return { data: data };
};

