import fetch from "isomorphic-unfetch";

const Index = props => {
  return (
    <div className="container m-4">
      {props.data}
      {/* {props.data.map(letter => {
        return (
          <div key={letter.letter_id}>
            <p>{letter.text}</p>
            <br />
            <p>{letter.fromFirstName}</p>
          </div>
        );
      })} */}
    </div>
  );
};

Index.getInitialProps = async () => {
  const response = await fetch("http://localhost:5500/api/letter/1");
  const data = await response.json();
  let formattedData = data[0].text.replace("morning", "TEST");
  //   data[0].text.replace("morning", "TEST");
  //   console.log(data);
  return { data: formattedData };
};

export default Index;
